import { Component } from '@angular/core';

@Component({
  selector: 'app-nino',
  templateUrl: './nino.component.html',
  styleUrls: ['./nino.component.css']
})
export class NinoComponent {
  this.category = "nino";
}
<p>nino works!</p>
<app-banner></app-banner>
<app-lista-productos [category]="category"></app-lista-productos>
