import { Component } from '@angular/core';

@Component({
  selector: 'app-topbar',
  templateUrl: './topbar.component.html',
  styleUrls: ['./topbar.component.css']
})
export class TopbarComponent {

}
<span>
  <div class="topnav">
    <a [routerLink]="['/']"><img src="/assets/Logo.png" class="logo" alt= "logo" /></a>
    <a routerLink="/hombre" class="pagelink">Men</a>
    <a routerLink="/mujer" class="pagelink">Women</a>
    <a routerLink="/nino" class="pagelink">Kids</a>
    <a routerLink="/carrito"><img src="/assets/carrito.jpg" class="cart" alt="carrito" /></a>
    <a><img src="/assets/cuenta.jpg" class="account" alt="cuenta" /></a>
    <input placeholder="search" class="searchBar" />
  </div>
</span>
