import { Component } from '@angular/core';

@Component({
  selector: 'app-banner',
  templateUrl: './banner.component.html',
  styleUrls: ['./banner.component.css']
})
export class BannerComponent {
  banners = ["assets/bannerImages/image1.jpeg","assets/bannerImages/image2.jpeg","assets/bannerImages/image3.jpeg"].map(img => `url(${img})`);

  images = [...this.banners];

  styles = this.images.map(url => ({
    backgroundPosition:'center center',
    backgroundRepeat:'no-repeat',
    backgroundSize:'cover',
    backgroundImage: url
  }));

  index = 0;
}
<hr>
  <img class="banner" src="assets/bannerImages/image1.jpeg" alt="banner"/>
