import { Component } from '@angular/core';

@Component({
  selector: 'app-banner',
  templateUrl: './banner.component.html',
  styleUrls: ['./banner.component.css']
})
export class BannerComponent {
  images = ["/assets/bannerImages/image1.jpeg","/assets/bannerImages/image2.jpeg","/assets/bannerImages/image3.jpeg"];

  styles = this.images.map(url => ({
    backgroundPosition:'center center',
    backgroundRepeat:'no-repeat',
    backgroundSize:'cover',
    backgroundImage: url
  }));

  index = 0;
}
<div class="banner" [ngStyle]="styles[index]"></div>
<span *ngFor="let image of images" [style.background]="image"><span>
