import { Component, OnInit } from '@angular/core';

import { productos } from '../producto';
import { CarritoService } from '../carrito.service';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-hombre',
  templateUrl: './hombre.component.html',
  styleUrls: ['./hombre.component.css']
})
export class HombreComponent implements OnInit {
  productos = [...productos];
  constructor(
    private carritoService: CarritoService
  ) { }

  infoProductos!: Observable<{ identificador: number, nombre: string, precio: number, descripcion: string }[]>;

  ngOnInit(): void {
    this.infoProductos =  this.cartService.getProductData();
  }
}
