import { Component } from '@angular/core';

@Component({
  selector: 'app-banner',
  templateUrl: './banner.component.html',
  styleUrls: ['./banner.component.css']
})
export class BannerComponent {
  cats = [
    'https://cdn.pixabay.com/photo/2019/11/08/11/56/cat-4611189_960_720.jpg',
    'https://cdn.pixabay.com/photo/2018/07/13/10/20/cat-3535404_960_720.jpg',
    'https://cdn.pixabay.com/photo/2016/11/14/19/20/kittens-1824367_960_720.jpg',
  ].map(img => `url(${img})`);
  dogs = [
    'https://cdn.pixabay.com/photo/2017/08/01/09/04/dog-2563759_960_720.jpg',
    'https://cdn.pixabay.com/photo/2015/07/31/11/43/bordeaux-869032_960_720.jpg',
    'https://cdn.pixabay.com/photo/2017/02/22/03/59/dog-2088424_960_720.jpg',
  ].map(img => `url(${img})`)

  images = [
    ...this.cats,
    ...this.dogs,
  ];
  
  styles = this.images.map(url => ({
    backgroundPosition:'center center',
    backgroundRepeat:'no-repeat',
    backgroundSize:'cover',
    backgroundImage: url
  }));

  index = 0;

  onSetIndex(i: number) {
    this.index = i;
  }
}
<div class="banner" [ngStyle]="styles[index]"></div>
<span *ngFor="let image of images" [style.background]="image"><span>
